import React, { Component } from "react";
import classnames from "classnames";
import {Menu, Button, Icon, Input} from "phoenix-ui";
import Code from "./code/code";

export default class menu extends Component{

    constructor(props,context){
        super(props,context);

        this.state = {
            visible: false,
            placement: 'top',
            align: 'left',
            width: 40,
            closeButton: false,
            navAlign: 'top',
            activeName: 'index',
            value: 'ha'
        }
    }

    onMenuChange(visible){
        console.log(visible);
    }

    switchAlign(){
        let newAlign = "left";

        if(this.state.align == "left") newAlign="right";

        this.setState({
            align: newAlign
        });
    }

    switchPlacement(){
        this.setState({
            placement: "left-full"
        });
    }

    switchWidth(){
        this.setState({
            width: 50
        });
    }

    switchCloseButton(){
        this.setState({
            closeButton: !this.state.closeButton
        });
    }

    switchNavAlign(){
        this.setState({
            navAlign: 'center'
        });
    }

    switchActiveName(){
        this.setState({
            activeName: 'menu'
        });
    }

    // 
    setActiveName(name){
        this.setState({
            activeName: name,
            visible: false // false
        });
    }

    onMenuItemChange(name){
        console.log(name);
    }

    onInputChange(e){
        this.setState({
            value: e.target.value
        });
    }

    render(){
        return(
            <div>
                <Menu visible={this.state.visible} scrollCeiling={100} onMenuChange={::this.onMenuChange}>
                    <Menu.Header align={this.state.align}>Phoenix</Menu.Header>
                    <Menu.Body width={this.state.width} placement={this.state.placement} closeButton={this.state.closeButton}>
                        <Menu.Nav align={this.state.navAlign}>
                            <div>
                                <Input placeholder="" value={this.state.value} onChange={::this.onInputChange}/>
                            </div>
                            <Menu.List activeName={this.state.activeName} onMenulistChange={::this.setActiveName}>
                                <Menu.Item name="index" phIcon="home"></Menu.Item>
                                <Menu.Item name="menu" href="#menu" phIcon="menu" onChange={::this.onMenuItemChange}></Menu.Item>
                            </Menu.List>
                        </Menu.Nav>
                    </Menu.Body>
                </Menu>

                <h2 className="comp-title">Menu</h2>
                <h3 className="comp-type"><strong>Menu</strong></h3>
                <h3 className="comp-type">visible(false) </h3>
                <Code target="menu-visible" />

                <h3 className="comp-type">scrollCeiling </h3>
                <h3 className="comp-tip">100</h3>
                <Code target="menu-scrollceiling" />

                <h3 className="comp-type">onMenuChange </h3>
                <Code target="menu-onmenuchange" />


                <h3 className="comp-type"><strong>MenuHeader</strong></h3>
                <h3 className="comp-type">align(left) </h3>
                <div className="content">
                    <Button onClick={::this.switchAlign}>align:left</Button>
                </div>
                <Code target="menu-header-align" />


                <h3 className="comp-type"><strong>MenuBody</strong></h3>
                <h3 className="comp-type">placement(top) </h3>
                <div className="content">
                    <Button onClick={::this.switchPlacement}>placement:top->left-full</Button>
                </div>
                <Code target="menu-body-placement" />

                <h3 className="comp-type">width(50) </h3>
                <div className="content">
                    <Button onClick={::this.switchWidth}>width:40->50</Button>
                </div>
                <Code target="menu-body-width" />

                <h3 className="comp-type">closeButton </h3>
                <div className="content">
                    <Button onClick={::this.switchCloseButton}>closeButton:false</Button>
                </div>
                <Code target="menu-body-closebutton" />


                <h3 className="comp-type"><strong>MenuNav</strong></h3>
                <h3 className="comp-type">align(top) </h3>
                <div className="content">
                    <Button onClick={::this.switchNavAlign}>align:top->center</Button>
                </div>
                <Code target="menu-nav-align" />


                <h3 className="comp-type"><strong>MenuList</strong></h3>
                <h3 className="comp-type">activeName(null) </h3>
                <div className="content">
                    <Button onClick={::this.switchActiveName}>activeName:index->menu</Button>
                </div>
                <Code target="menu-list-onmenulistchange" />

                <h3 className="comp-type">onMenulistChange activeName</h3>
                <Code target="menu-list-onmenulistchange" />


                <h3 className="comp-type"><strong>MenuItem</strong></h3>
                <h3 className="comp-type">phIcon() </h3>
                <Code target="menu-item-phicon" />

                <h3 className="comp-type">name() </h3>
                <Code target="menu-item-name" />

                <h3 className="comp-type">href() </h3>
                <Code target="menu-item-href" />

                <h3 className="comp-type">onMenuitemChange </h3>
                <Code target="menu-item-onmenuitemchange" />

            </div>
        );
    }
}

