import React, { Component } from "react";
import {LoadingList} from "phoenix-ui";
import Code from "./code/code";
import {ajax} from "./utils/tool.js";

export default class loadinglist extends Component{
    constructor(props,context){
        super(props,context); 

        this.count = 0;

        this.state = {
            loadingData : [],
            loadingStatus: 1 // 0"",1"",2"",3"",4""
        }

        this.getData("./data/post.json");
    }

    getData(fileName){ // ajax
        ajax(fileName).then((json)=>{
            let data = json.data;
            let loadingData = this.state.loadingData.slice(0);
            for(let i in data){
                loadingData.push(data[i]);
            }
            this.setState({
                loadingData: loadingData,
                loadingStatus: json.final? 4:2
            }); 

        }, (error)=>{// 
            this.setState({
                loadingStatus: 3
            }); 
            console.error("", error)
        });
    }

    onLoading(){ // ()

        // 
        if(this.state.loadingStatus==4) return;

        // 
        this.setState({
            loadingStatus: 1
        }); 

        if(this.state.loadingStatus!=3) this.count++; // json
        this.getData("./data/post"+this.count+".json"); // 
    }

    onLoadingEnd(){ // DOM
        if(this.state.loadingStatus==3) return;

        this.setState({
            loadingStatus: 0
        }); 
    }

    render(){
        return(
            <div>
            	<h2 className="comp-title">LoadingList</h2>
                <h3 className="comp-type">phMode(auto) [auto,button]</h3>
                <h3 className="comp-type">loadingStatus(1) 0, 1, 2, 3, 4</h3>
                <h3 className="comp-type">buttonStyles ["primary","info","success","error","gray"]</h3>
                <h3 className="comp-type">loadTips ["","","","",""]</h3>
                <h3 className="comp-type">onLoading </h3>
                <h3 className="comp-type">onLoadingEnd </h3>
                <LoadingList phMode="auto" loadingStatus={this.state.loadingStatus} 
                    loadTips={["","...","","",""]} 
                    buttonStyles={["primary","gray","success","danger","gray"]} 
                    onLoading={::this.onLoading} onLoadingEnd={::this.onLoadingEnd}>
                    <ul>
                        {
                            this.state.loadingData.map((data,index)=>{
                                return (
                                    <li key={index}>
                                        <img src={data.image} alt={data.title} />
                                        <p>{data.title}</p>
                                        <p>{data.desp}</p>
                                    </li>
                                );
                            })
                        }
                    </ul>
                </LoadingList>
                <Code target="loadinglist" />
            </div>
        );
    }
}

